  /**
   * @dev Create a new token hold with expiration date.
   */
  function holdWithExpirationDate(
    address token,
    bytes32 holdId,
    address recipient,
    address notary,
    bytes32 partition,
    uint256 value,
    uint256 expiration,
    bytes32 secretHash,
    bytes calldata certificate
  )
    external
    returns (bool)
  {
    _checkExpiration(expiration);

    return _createHold(
      token,
